import {Link} from "react-router-dom"
const Navbar = ({ darkMode, toggleDarkMode }) => {
    const scrollToSection = (id) => {
      const element = document.getElementById(id);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    };

    return (<nav className ="nav">
        {/*turn into toggle graphic in the future*/}
        <button className="navbar_btn toggle-dark-mode" onClick={toggleDarkMode}>
          <h3 className='navbar_txt'>{darkMode ? 'Inverted' : 'Regular'}</h3>
        </button>

        <ul>
        <Link to="/"><h3 className='navbar_txt'>Home</h3></Link>
        <Link to="/allprojects"><h3 className='navbar_txt'>All Projects</h3></Link>
<button
  style={{
    fontSize: '1rem',
    fontWeight: 'lighter',
    color: 'var(--blue2black)',
    backgroundColor: 'var(--black2white)',
    border: 'none',
    padding: '0'  // Set padding to 0 to remove padding around text
  }}
  onClick={() => scrollToSection('experience')}
>
  Experience
</button>
        <Link to="/beyondtech" className='navbar_txt'>Beyond Tech</Link>
        <a className="mailto" href="mailto:tranviviana@berkeley.edu"><h3 className='navbar_txt'>Contact</h3></a>
        <button className='navbar_txt_btn'>
        <Link to="/resume" className="nav-link">Resume</Link>
      </button>

        </ul>
    </nav>
)}
export default Navbar;
